}

const message = RED.util.cloneMessage(msg)

message.reset = true
return message

const messages = []
const message = RED.util.cloneMessage(msg)
const count = context.get("count") + 1

message.retain = true
message.label = 'default'
messages.push(message)

messages.push({
    payload: {
        setting: msg.topic,
        value: msg.payload
    },
