return [messages]

const value = msg.payload.trim()

if (value != 'connected') {
    node.status({ fill: "grey", shape: "dot", text: value })
    return null
}

node.status({ fill: "blue", shape: "dot", text: "connected" })

return {
    payload: true,
    topic: 'connected'
}

const messages = []

function errorMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/error',
        retain: false
    }
}

function successMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/key',
        retain: false
    }
}

if (Array.isArray(msg.payload)) {

    for (let result of msg.payload) {

        if (Object.prototype.hasOwnProperty.call(result, 'success')) {

            messages.push(successMessage(result.success.username))

        }

        if (Object.prototype.hasOwnProperty.call(result, 'error')) {

            messages.push(errorMessage(result.error.description))

        }
    }

} else {

    messages.push(errorMessage(msg.payload))

}

node.status({
    text: Object.keys(messages).length,
    shape: 'dot',
    fill: 'blue'
})

return [messages]

const address = msg.payload

return {
    bridgeAddress: address,
    url: 'https://' + address + '/api',
    payload: {
        devicetype: 'nodered',
        generateclientkey: true
    },
    method: 'POST'
}

const messages = []

for (let address of (msg.payload.addresses ?? [])) {

    if (/^\d{1,3}(\.\d{1,3}){3}$/.exec(address)) {

        const message = RED.util.cloneMessage(msg)

        delete message.payload.addreses
        message.payload.address = address
        message.topic = 'hue/' + address
        message.retain = false
        messages.push(message)

    }
}

return [messages]

function cloneMessage() {

    return [
        {
        topic: 'powerview/error',
        payload: RED.util.cloneMessage(msg)
    },
    {
        topic: 'powerview/status',
        payload: 2
    }
    ]
}

let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return cloneMessage()

    }

    if ((msg.statusCode < 200) || (msg.statusCode >= 300)) {

        fill = 'red'
        return cloneMessage()

    }

    if (msg.statusCode != 200) {

        fill = 'yellow'
        return cloneMessage()

    }

    return {
        topic: 'powerview/status',
        payload: 1
    }

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

let model = []

function findShades(roomId) {
    const shades = []
    for (const shade of msg.payload.shadeData) {
        if (shade.roomId == roomId) {
            shades.push({
                title: shade.name,
                id: shade.id,
                category: 'shade'
            })
        }
    }
    return shades.sort((a, b) => {
        return a.title.localeCompare(b.title)

    })
}

function findScenes(roomId) {
    const scenes = []
    for (const scene of msg.payload.sceneData) {
        if (scene.roomId == roomId) {
            scenes.push({
                title: scene.name,
                id: scene.id,
                category: 'scene'
            })
        }
    }
    return scenes.sort((a, b) => {
        return a.title.localeCompare(b.title)

    })
}

for (const room of msg.payload.roomData) {
    const scenes = findScenes(room.id)
    const shades = findShades(room.id)
    if ((scenes.length > 0) || (shades.length > 0)) {
        model.push({
            title: room.name,
            id: room.id,
            children: scenes,
            shades: shades,
            category: 'group'
        })
    }
}

model = model.sort((a, b) => {
    return a.title.localeCompare(b.title)
})

flow.set('model', model)

return {
    payload: model,
    topic: 'powerview/model',
    retain: true
}

return {
    payload: 0,
    topic: 'powerview/status'
}
