/*global suncalc*/

const LATITUDE = env.get('LATITUDE')
const LONGITUDE = env.get('LONGITUDE')

const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const now = msg.payload.now
const hour = msg.payload['settings/bedtime']
const date = new Date(now)
const events = [{ cancel: true }]
const debug = []

suncalc.addTime(40, "midday", "ignore1")
suncalc.addTime(45, "ignore2", "afternoon")

const times = suncalc.getTimes(
    new Date(
        date.getFullYear(),
        date.getMonth(),
        date.getDate(),
        1,
        1
    ),
    LATITUDE,
    LONGITUDE
)

for (let key in times) {

    if (Object.prototype.hasOwnProperty.call(times, key)) {

        const time = times[key].getTime()

        const message = {
            topic: 'timer/time',
            payload: key,
            at: time,
        }

        debug.push(message)

        if (time > now) {
            events.push(message)
        }
    }
}

const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)

const bedtime = new Date(
    date.getFullYear(),
    date.getMonth(),
    date.getDate(),
    hour).getTime() + offset

const bedtimeEvent = {
    payload: 'bedtime',
    topic: 'timer/time',
    at: bedtime
}

debug.push(bedtimeEvent)

if (bedtime > now) {

    events.push(bedtimeEvent)
}

events.sort((a, b) => {

    if (!Object.prototype.hasOwnProperty.call(a, 'at')) {
        return b
    }

    if (!Object.prototype.hasOwnProperty.call(b, 'at')) {
        return a
    }

    return a.at - b.at
})

const month = date.getMonth() + 1
const day = date.getDate()
let theme = 'standard'

if ((month == 7) && (day <= 4)) {

    theme = 'tribal'

} else if (month == 10) {

    theme = 'spooky'

} else if (month == 12) {

    theme = 'jolly'

}

events.push({
    payload: theme,
    topic: 'timer/theme'
})

return [
    events,
    debug,
    {
        payload: theme,
        topic: 'debug/timer/theme',
        retain: true
    }
]

const timers = context.get('timers')

if (msg.cancel) {

    timers.forEach(id => clearTimeout(id))
    context.set('timers', [])

    node.status({
        text: 0,
        shape: 'ring',
        fill: 'green'
    })

    return null

}

if (msg.at === undefined) {
    return msg
}

const delay = msg.at - new Date().getTime()

if (delay <= 0) {

    return msg

}

const id = setTimeout(() => {

    let timers = context.get('timers')

    timers = timers.filter(timer => timer != id)
    context.set('timers', timers)

    node.status({
        text: timers.length,
        shape: 'dot',
        fill: 'green'
    })

    node.send(msg)

}, delay)

timers.push(id)
context.set('timers', timers)

node.status({
    text: timers.length,
    shape: 'dot',
    fill: 'blue'
})

return null

function copyMsg() {

    return {
        payload: msg.payload,
        topic: msg.topic,
        retain: false
    }
}

if (msg.retain) {

    node.status({ fill: 'grey', shape: 'dot', text: 'retained' });
    return [null, null, null, null, null, null]

}

if (msg.topic != 'automation/trigger') {

    node.status({ fill: 'grey', shape: 'dot', text: 'different topic' });
    return [null, null, null, null, null, null]

}

if (!msg.payload['settings/shades']) {

    node.status({ fill: 'grey', shape: 'dot', text: 'shades disabled' });
    return [null, null, null, null, null, null]

}

switch (msg.payload['timer/time']) {

    case 'sunrise':
        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [copyMsg(), null, null, null, null, null]

    case 'midday':
        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, copyMsg(), null, null, null, null]

    case 'afternoon':
        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, null, copyMsg(), null, null, null]

    case 'sunset':
        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, null, null, copyMsg(), null, null]

    case 'dusk':
        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, null, null, null, copyMsg(), null]

    case 'bedtime':

        node.status({ fill: 'blue', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, null, null, null, null, copyMsg()]

    default:

        node.status({ fill: 'grey', shape: 'dot', text: msg.payload['timer/time'] });
        return [null, null, null, null, null, null]

}

