function copyMsg() {

    node.status({
        text: new Date(msg.timestamp).toLocaleString(),
        shape: 'dot',
        fill: 'blue'
    })

    return {
        payload: msg.payload,
        topic: msg.topic,
        retain: false
    }
}

if (msg.retain) {

    return [null, null, null, null]

}

if (msg.topic != 'automation/trigger') {

    return [null, null, null, null]

}

if (!msg.payload['settings/shades']) {

    return null

}

switch (msg.payload["timer/time"]) {

    case 'sunrise':

        return [copyMsg(), null, null, null]

    case 'midday':

        return [null, copyMsg(), null, null]

    case 'afternoon':

        return [null, null, copyMsg(), null]

    case 'bedtime':

        return [null, null, null, copyMsg()]

    default:

        return [null, null, null, null]

}

function copyMsg() {

    node.status({
        text: new Date(msg.timestamp).toLocaleString(),
        shape: 'dot',
        fill: 'blue'
    })

    return {
        payload: msg.payload,
        topic: msg.topic,
        retain: false
    }
}

if (msg.retain) {

    return [null, null, null]

}

if (msg.topic != 'automation/trigger') {

    return [null, null, null]

}

if (!msg.payload['settings/lighting']) {

    return [null, null, null]

}

switch (msg.payload["timer/time"]) {

    case 'sunrise':

        return [copyMsg(), null, null]

    case 'sunset':

        return [null, copyMsg(), null]

    case 'bedtime':

        return [null, null, copyMsg()]

    default:

        return [null, null, null]

}

/*global suncalc*/

// delay execution for 1 second in order to
// give the 'delete retained timer events'
// function node time to send its messages
// ahead of this one's
await new Promise(resolve => setTimeout(resolve, 1000))

const LATITUDE = env.get('LATITUDE')
const LONGITUDE = env.get('LONGITUDE')

const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const now = msg.payload.now
const hour = msg.payload['settings/bedtime']
const date = new Date(now)
const messages = [{ clear: true }]

const times = suncalc.getTimes(
    new Date(
        date.getFullYear(),
        date.getMonth(),
        date.getDate(),
        1,
        1
    ),
    LATITUDE,
    LONGITUDE
)

const sunrise = times.sunrise.getTime()
const sunset = times.sunset.getTime()
let midday = null
let afternoon = null

node.status('sunrise: ' + times.sunrise.toLocaleString())

if (sunrise > now) {

    messages.push({
        payload: 'sunrise',
        topic: 'timer/time',
        at: sunrise
    })
}

for (let time = sunrise; time < sunset; time += 60000) {

    const position = suncalc.getPosition(new Date(time), LATITUDE, LONGITUDE)

    if ((midday === null) && (position.altitude > 0.8)) {

        midday = time
        node.status('midday: ' + new Date(time).toLocaleString())

        if (midday > now) {
            messages.push({
                payload: 'midday',
                topic: 'timer/time',
                at: midday
            })
        }
    }

    if ((afternoon == null) && (position.azimuth > 0) && (position.altitude < 0.8)) {

        afternoon = time
        node.status('afternoon: ' + new Date(time).toLocaleString())

        if (afternoon > now) {

            messages.push({
                payload: 'afternoon',
                topic: 'timer/time',
                at: afternoon
            })
        }

        break

    }
}

node.status('sunset: ' + times.sunset.toLocaleString())

if (sunset > now) {

    messages.push({
        payload: 'sunset',
        topic: 'timer/time',
        at: sunset
    })
}

const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)

const bedtime =
    new Date(
        date.getFullYear(),
        date.getMonth(),
        date.getDate(),
        hour).getTime() + offset

node.status('bedtime: ' + new Date(bedtime).toLocaleString())

if (bedtime > now) {

    messages.push({
        payload: 'bedtime',
        topic: 'timer/time',
        at: bedtime
    })
}

const month = date.getMonth() + 1
const day = date.getDate()
let theme = 'standard'

if ((month == 7) && (day <= 4)) {

    theme = 'tribal'

} else if (month == 10) {

    theme = 'spooky'

} else if (month == 12) {

    theme = 'jolly'

}

node.status('theme: ' + theme)

messages.push({
    payload: theme,
    topic: 'timer/theme'
})

return [messages]

const timers = context.get('timers')

if (msg.clear) {

    timers.forEach(id => clearTimeout(id))
    context.set('timers', [])

    node.status({
        text: 0,
        shape: 'ring',
        fill: 'green'
    })

    return null

}

const at = (msg.at ? new Date(msg.at) : new Date())
const now = new Date()
const delay = at.getTime() - now.getTime()

if (delay <= 0) {

    return msg

}

const id = setTimeout(() => {

    let timers = context.get('timers')

    timers = timers.filter(timer => timer != id)
    context.set('timers', timers)

    node.status({
        text: timers.length,
        shape: 'dot',
        fill: 'green'
    })

    node.send(msg)

}, delay)

timers.push(id)
context.set('timers', timers)

node.status({
    text: timers.length,
    shape: 'dot',
    fill: 'blue'
})

return null

const parts = msg.status.text.split(': ', 2)

if (parts.length > 1) {

    return {
        payload: parts[1],
        topic: 'timer/' + parts[0],
        retain: true
    }
}

return null

function cloneMessage() {

    return {
        topic: 'powerview/error',
        payload: RED.util.cloneMessage(msg)
    }
}

let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return cloneMessage()

    }

    if ((msg.statusCode < 200) || (msg.statusCode >= 300)) {

        fill = 'red'
        return cloneMessage()

    }

    if (msg.statusCode != 200) {

        fill = 'yellow'
        return cloneMessage()

    }

    return null

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

let model = []

function findScenes(roomId) {

    const scenes = []

    for (const scene of msg.payload.sceneData) {

        if (scene.roomId == roomId) {

            scenes.push({
                name: scene.name,
                id: scene.id
            })
        }
    }

    return scenes.sort((a, b) => {

        return a.name.localeCompare(b.name)

    })
}

for (const room of msg.payload.roomData) {

    const scenes = findScenes(room.id)

    if (scenes.length > 0) {

        model.push({
            name: room.name,
            id: room.id,
            scenes: scenes
        })
    }
}

model = model.sort((a, b) => {

    return a.name.localeCompare(b.name)

})

return {
    payload: model,
    topic: 'powerview/model',
    retain: true
}

const messages = []

if (Object.prototype.hasOwnProperty.call(msg.payload, 'addresses') && Array.isArray(msg.payload.addresses)) {

    for (let address of msg.payload.addresses) {

        const message = RED.util.cloneMessage(msg)

        delete message.payload.addresses
        message.topic = address
        messages.push(message)

    }
}

return [messages]

const messages = []

function errorMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/error',
        retain: false
    }
}

function successMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/key',
        retain: false
    }
}

if (Array.isArray(msg.payload)) {

    for (let result of msg.payload) {

        if (Object.prototype.hasOwnProperty.call(result, 'success')) {

            messages.push(successMessage(result.success.username))

        }

        if (Object.prototype.hasOwnProperty.call(result, 'error')) {

            messages.push(errorMessage(result.error.description))

        }
    }

} else {

    messages.push(errorMessage(msg.payload))

}

node.status({
    text: Object.keys(messages).length,
    shape: 'dot',
    fill: 'blue'
})

return [messages]

const discovered = msg.payload.discovered || {}
const bridges = flow.get('bridges') || {}

for (let address in discovered) {

    const bridge = bridges[address] || {}
    const b = discovered[address]

    for (let property in b) {

        bridge[property] = b[property]

    }

    bridges[address] = bridge

}

for (let topic in msg.payload) {

    const matches = /^hue\/([^ /]+)\/status$/.exec(topic)

    if (Array.isArray(matches) && (matches.length == 2)) {

        const address = matches[1]
        const bridge = bridges[address] || {}

        bridge.status = msg.payload[topic]
        bridges[address] = bridge

    }
}

flow.set('bridges', bridges)

const model = []

for (let address in bridges) {

    const bridge = bridges[address]

    if (bridge.txtRecord) {

        model.push({
            id: bridge.txtRecord.bridgeid,
            model: bridge.txtRecord.modelid,
            name: bridge.name,
            host: bridge.host,
            address: address,
            port: bridge.port,
            status: bridge.status
        })
    }
}

return {
    payload: model,
    topic: 'hue/bridges',
    retain: true
}

const address = msg.payload

return {
    bridgeAddress: address,
    url: 'https://' + address + '/api',
    payload: {
        devicetype: 'nodered',
        generateclientkey: true
    },
    method: 'POST'
}
