const date = new Date(msg.payload.daily)
const month = date.getMonth() + 1
const day = date.getDate()
const message = RED.util.cloneMessage(msg)

if ((month == 7) && (day <= 4)) {

    message.payload = 'tribal'

} else if (month == 10) {

    message.payload = 'spooky'

} else if (month == 12) {

    message.payload = 'jolly'

} else {

    message.payload = 'standard'

}

node.status({
    text: message.payload,
    shape: 'dot',
    fill: 'blue'
})

message.topic = 'daily/theme'
message.retain = true
return message

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = new Date(msg.payload.now)
const sunrise = RED.util.cloneMessage(msg)
const sunset = RED.util.cloneMessage(msg)

const times = suncalc.getTimes(
    // work around bug in suncalc
    new Date(
        now.getFullYear(),
        now.getMonth(),
        now.getDate(),
        1,
        1
    ),
    LATITUDE,
    LONGITUDE
)

sunrise.payload = times.sunrise.getTime()
sunrise.topic = 'daily/sunrise'
sunrise.retain = true

sunset.payload = times.sunset.getTime()
sunset.topic = 'daily/sunset'
sunset.retain = true

const status =
    new Date(sunrise.payload).toLocaleTimeString() +
    ' | ' +
    new Date(sunset.payload).toLocaleTimeString()

node.status({
