})

return [sunrise, sunset]

const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const now = new Date(msg.payload.now)
const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)
const time = new Date(
    now.getFullYear(),
    now.getMonth(),
    now.getDate(),
    msg.payload['settings/bedtime']
).getTime()

const message = RED.util.cloneMessage(msg)

message.payload = time + offset,
message.topic = 'daily/bedtime'
message.retain = true

node.status({
    text: new Date(message.payload).toLocaleTimeString(),
    shape: 'dot',
    fill: 'blue'
})

return message

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = msg.payload.now
const sunrise = msg.payload['daily/sunrise']
const sunset = msg.payload['daily/sunset']
const bedtime = msg.payload['daily/bedtime']

const position = suncalc.getPosition(
    new Date(now),
    LATITUDE,
    LONGITUDE)

const message = RED.util.cloneMessage(msg)

if ((now <= sunrise) || (now >= bedtime)) {

    message.payload = 'night'

} else if (now >= sunset) {

    message.payload = 'evening'

} else if (position.altitude > 0.8) {

    message.payload = 'midday'

} else if (position.azimuth > 0) {

    message.payload = 'afternoon'

} else {

    message.payload = 'morning'

}

node.status({
    text: message.payload,
    shape: 'dot',
    fill: 'blue'
})

message.topic = 'timer/time'
message.retain = true
return message

if (msg.retain) {

    return null
}

if (msg.topic != 'automation/trigger') {

    return null

}

if (!msg.payload['settings/shades']) {

    return null

}

switch (msg.payload["timer/time"]) {

    case 'morning':

        return [RED.util.cloneMessage(msg), null, null, null]

    case 'midday':

        return [null, RED.util.cloneMessage(msg), null, null]

    case 'afternoon':

        return [null, null, RED.util.cloneMessage(msg), null]

    case 'night':

        return [null, null, null, RED.util.cloneMessage(msg)]

    default:

        return null

}

if (msg.retain) {

    return null
}

if (msg.topic != 'automation/trigger') {

    return null

}

if (!msg.payload['settings/lighting']) {

    return null

}

switch (msg.payload["timer/time"]) {

    case 'morning':

        return [RED.util.cloneMessage(msg), null, null, null]

    case 'evening':

        return [null, RED.util.cloneMessage(msg), null, null]

    case 'night':

        return [null, null, RED.util.cloneMessage(msg), null]

    default:

        return null

}

const message = {
    topic: 'powerview/error',
    payload: RED.util.cloneMessage(msg)
}

let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return message

    }

    if ((msg.statusCode < 200) || (msg.statusCode >= 300)) {

        fill = 'red'
        return message

    }

    if (msg.statusCode != 200) {

        fill = 'yellow'
        return message

    }

    return null

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

let model = []

function findScenes(roomId) {

    const scenes = []

    for (const scene of msg.payload.sceneData) {

        if (scene.roomId == roomId) {

            scenes.push({
                name: scene.name,
                id: scene.id
            })
        }
    }

    return scenes.sort((a, b) => {

        return a.name.localeCompare(b.name)

    })
}

for (const room of msg.payload.roomData) {

    const scenes = findScenes(room.id)

    if (scenes.length > 0) {

        model.push({
            name: room.name,
            id: room.id,
            scenes: scenes
        })
    }
}

model = model.sort((a, b) => {

    a.name.localeCompare(b.name)

})

const message = RED.util.cloneMessage(msg)

message.payload = model
message.topic = 'powerview/model'
message.retain = true
message.info = env.get('POWERVIEW_ADDRESS')
return message

const messages = []

for (let address of msg.payload.addresses) {

    const message = RED.util.cloneMessage(msg)

    delete message.payload.addresses
    message.topic = address
    messages.push(message)

}

return [messages]

const successTopic = 'hue/' + msg.bridgeAddress + '/key'
const messages = []

function errorMessage(payload) {

    const message = RED.util.cloneMessage(msg)

    message.payload = payload
    message.topic = 'hue/' + msg.bridgeAddress + '/error'
    message.retain = false
    return message

}

if (Array.isArray(msg.payload)) {

    for (let result of msg.payload) {

        if (Object.prototype.hasOwnProperty.call(result, 'success')) {

            const message = RED.util.cloneMessage(msg)

            message.payload = result.success.username
            message.topic = successTopic
            message.retain = true
            messages.push(message)

        }

        if (Object.prototype.hasOwnProperty.call(result, 'error')) {

            messages.push(errorMessage(result.error.description))

        }
    }

} else {

    messages.push(errorMessage(msg.payload))

}

node.status({
    text: Object.keys(messages).length,
    shape: 'dot',
    fill: 'blue'
})

return [messages]

const discovered = msg.payload.discovered || {}
const bridges = flow.get('bridges') || {}

for (let address in discovered) {

    const bridge = bridges[address] || {}
    const b = discovered[address]

    for (let property in b) {

        bridge[property] = b[property]

    }

    bridges[address] = bridge

}

for (let topic in msg.payload) {

    const matches = /^hue\/([^ /]+)\/status$/.exec(topic)

    if (Array.isArray(matches) && (matches.length == 2)) {

        const address = matches[1]
        const bridge = bridges[address] || {}

        bridge.status = msg.payload[topic]
        bridges[address] = bridge

    }
}

flow.set('bridges', bridges)

const model = []

for (let address in bridges) {

    const bridge = bridges[address]

    if (bridge.txtRecord) {

        model.push({
            id: bridge.txtRecord.bridgeid,
            model: bridge.txtRecord.modelid,
            name: bridge.name,
            host: bridge.host,
            address: address,
            port: bridge.port,
            status: bridge.status
        })
    }
}

const message = RED.util.cloneMessage(msg)

message.payload = model
message.topic = 'hue/bridges'
message.retain = true
return message
