function cloneMessage() {

    return {
        payload: RED.util.cloneMessage(msg),
        topic: 'hue/' + env.get('ADDRESS') + '/error',
        retain: true
    }
}

if (!Number.isInteger(msg.statusCode)) {

    return cloneMessage()

}

if (msg.statusCode != 200) {

    return cloneMessage()

}

if (!Object.prototype.hasOwnProperty.call(msg.payload, 'data')) {

    return cloneMessage()

}

if (!Object.prototype.hasOwnProperty.call(msg.payload, 'errors')) {

    return cloneMessage()

}

if (msg.payload.errors.length > 0) {

    return cloneMessage()

}

return null

// clear out cached resources
flow.set('resources', {})

const updates = [{ type: 'update', data: msg.payload }]
const event = { type: 'message', data: JSON.stringify(updates) }

return {
    payload: event,
    topic: 'message'
}

const TOPIC = 'hue/' + env.get('ADDRESS')
const resources = {}
const data = JSON.parse(msg.payload.data)
const messages = []

resources.payload = flow.get('resources')

for (let datum of data) {

    for (let value of datum.data) {

        const message = {
            topic: TOPIC + '/resource/' + value.type + '/' + value.id,
            retain: false
        }

        const category = resources.payload[value.type] || {}
        let resource = category[value.id] || {}

        switch (datum.type) {

            case 'add':
            case 'update':

                for (let property in value) {

                    resource[property] = value[property]
                }

                category[value.id] = message.payload = resource
                break

            case 'delete':

                delete category[value.id]
                message.payload = ''
                break

            default:

                node.warn(JSON.stringify(datum, undefined, 1))
                break

        }

        resources.payload[value.type] = category
        messages.push(message)

    }
}

flow.set('resources', resources.payload)
return [resources, messages]

const ADDRESS = env.get('ADDRESS')
const TOPIC = 'hue/' + ADDRESS
const MODEL = TOPIC + '/model'
const PUT = 'put/' + TOPIC + '/resource'

const message = {
    payload: { groups: [], title: env.get('TITLE') },
    topic: MODEL,
    retain: true
}

function findOwner(grouped_light) {

    const groups = msg.payload[grouped_light.owner.rtype]

    if (Object.prototype.hasOwnProperty.call(groups, grouped_light.owner.rid)) {

        return groups[grouped_light.owner.rid]

    }

    throw 'no container found for grouped_light ' + grouped_light.id

}

function findScenes(owner) {

    const scenes = []

    for (let sceneId in msg.payload.scene) {

        const scene = msg.payload.scene[sceneId]

        if (scene.group.rid == owner.id) {

            scene.value = PUT + '/scene/' + scene.id
            scenes.push(scene)

        }
    }

    return scenes.sort((a, b) => {

        return a.metadata.name.localeCompare(b.metadata.name)

    })

}

for (let grouped_light_id in msg.payload.grouped_light) {

    const grouped_light = msg.payload.grouped_light[grouped_light_id]
    const owner = findOwner(grouped_light)
    const scenes = findScenes(owner)

    message.payload.groups.push({
        grouped_light: grouped_light,
        owner: owner,
        scenes: scenes,
        name: (owner.metadata ? owner.metadata.name : 'All Lights'),
        value: PUT + '/grouped_light/' + grouped_light.id
    })

}

message.payload.groups = message.payload.groups.sort((a, b) => {

    return a.name.localeCompare(b.name)

})

return message

if (msg.topic == 'controls/refresh') {

    return [
        { payload: msg.payload },
        null
    ]

}

const re = new RegExp('^put/hue/' + env.get('ADDRESS') + '/(resource/.+)$')
const matches = re.exec(msg.topic)

if (Array.isArray(matches) && (matches.length == 2)) {

    return [
        null,
        {
            payload: msg.payload,
            topic: matches[1],
            method: msg.method
        }
    ]

}

return null

const KEY = env.get('KEY')
const ADDRESS = env.get('ADDRESS')

return {
    payload: KEY,
    topic: 'hue/' + ADDRESS + '/key',
    retain: true,
    bridgeAddress: ADDRESS
}

return {
    payload: {
        url: 'https://' + env.get('ADDRESS') + '/eventstream/clip/v2',
        initDict: {
            withCredentials: false,
            https: {
                rejectUnauthorized: false
            },
            headers: {
                'hue-application-key': env.get('KEY')
            }
        }
    }
}

const message = RED.util.cloneMessage(msg)

message.url ='https://' + env.get('ADDRESS') + '/clip/v2/' + msg.topic
message.headers = { "hue-application-key": env.get('KEY') }

return message

const messages = []

for (let command of env.get('COMMANDS')) {

    const message = RED.util.cloneMessage(msg)

    message.payload = command.payload
    message.topic = command.topic
    message.method = command.method
    messages.push(message)

}

node.status({
    text: messages.length,
    shape: 'dot',
    fill: 'blue'
})

return [messages]

const retained = flow.get('retained', 'file') || {}
const messages = []

node.status({
    text: Object.keys(retained).length,
    shape: 'dot',
    fill: 'blue'
})

for (let topic in retained) {

    const message = RED.util.cloneMessage(retained[topic])

    message._session = msg.status._session
    message.retain = true
    messages.push(message)

}

return [messages]

