const date = new Date(msg.payload.daily)
const month = date.getMonth() + 1
const day = date.getDate()
const message = {}

if ((month == 7) && (day <= 4)) {

    message.payload = 'tribal'

} else if (month == 10) {

    message.payload = 'spooky'

} else if (month == 12) {

    message.payload = 'jolly'

} else {

    message.payload = 'standard'

}

node.status({
    text: message.payload,
    shape: 'dot',
    fill: 'blue'
})

message.topic = 'daily/theme'
message.retain = true
return message

const MILLIS_PER_SECOND = 1000
const MILLIS_PER_MINUTE = 60 * MILLIS_PER_SECOND
const MILLIS_PER_HALF_HOUR = 30 * MILLIS_PER_MINUTE
const MILLIS_PER_HOUR = 2 * MILLIS_PER_HALF_HOUR

const now = new Date(msg.payload.now)
const offset = MILLIS_PER_HALF_HOUR - Math.floor(Math.random() * MILLIS_PER_HOUR)
const time = new Date(
    now.getFullYear(),
    now.getMonth(),
    now.getDate(),
    msg.payload['settings/bedtime']
).getTime()

const message = {}

message.payload = time + offset,
message.topic = 'daily/bedtime'
message.retain = true

node.status({
    text: new Date(message.payload).toLocaleTimeString(),
    shape: 'dot',
    fill: 'blue'
})

return message

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = msg.payload.now
const sunrise = msg.payload['daily/sunrise']
const sunset = msg.payload['daily/sunset']
const bedtime = msg.payload['daily/bedtime']

const position = suncalc.getPosition(
    new Date(now),
    LATITUDE,
    LONGITUDE)

const message = {}

if ((now <= sunrise) || (now >= bedtime)) {

    message.payload = 'night'

} else if (now >= sunset) {

    message.payload = 'evening'

} else if (position.altitude > 0.8) {

    message.payload = 'midday'

} else if (position.azimuth > 0) {

    message.payload = 'afternoon'

} else {

    message.payload = 'morning'

}

node.status({
    text: message.payload,
    shape: 'dot',
    fill: 'blue'
})

message.topic = 'timer/time'
message.retain = true
return message

/*global suncalc*/

const LATITUDE = Number.parseFloat(env.get('LATITUDE'))
const LONGITUDE = Number.parseFloat(env.get('LONGITUDE'))

const now = new Date(msg.payload.now)
const sunrise = {}
const sunset = {}

const times = suncalc.getTimes(
    // work around bug in suncalc
    new Date(
        now.getFullYear(),
        now.getMonth(),
        now.getDate(),
        1,
        1
    ),
    LATITUDE,
    LONGITUDE
)

sunrise.payload = times.sunrise.getTime()
sunrise.topic = 'daily/sunrise'
sunrise.retain = true

sunset.payload = times.sunset.getTime()
sunset.topic = 'daily/sunset'
sunset.retain = true

const status =
    new Date(sunrise.payload).toLocaleTimeString() +
    ' | ' +
    new Date(sunset.payload).toLocaleTimeString()

node.status({
    text: status,
    shape: 'dot',
    fill: 'blue'
})

return [sunrise, sunset]

function copyMsg() {

    return {
        payload: msg.payload,
        topic: msg.topic,
        retain: false
    }
}

if (msg.retain) {

    return null
}

if (msg.topic != 'automation/trigger') {

    return null

}

if (!msg.payload['settings/shades']) {

    return null

}

switch (msg.payload["timer/time"]) {

    case 'morning':

        return [copyMsg(), null, null, null]

    case 'midday':

        return [null, copyMsg(), null, null]

    case 'afternoon':

        return [null, null, copyMsg(), null]

    case 'night':

        return [null, null, null, copyMsg()]

    default:

        return null

}

function copyMsg() {

    return {
        payload: msg.payload,
        topic: msg.topic,
        retain: false
    }
}

if (msg.retain) {

    return null
}

if (msg.topic != 'automation/trigger') {

    return null

}

if (!msg.payload['settings/lighting']) {

    return null

}

switch (msg.payload["timer/time"]) {

    case 'morning':

        return [copyMsg(), null, null, null]

    case 'evening':

        return [null, copyMsg(), null, null]

    case 'night':

        return [null, null, copyMsg(), null]

    default:

        return null

}

const payload = {}

payload['settings/shades'] = msg.payload['settings/shades']
payload['settings/lighting'] = msg.payload['settings/lighting']
payload['daily/theme'] = msg.payload['daily/theme']
payload['timer/time'] = msg.payload['timer/time']

return {
    payload: payload,
    topic: 'automation/trigger',
    retain: true
}

function cloneMessage() {

    return {
        topic: 'powerview/error',
        payload: RED.util.cloneMessage(msg),
        retain: true
    }
}

let fill = 'green'

try {

    if ((typeof msg.statusCode) != 'number') {

        fill = 'red'
        return cloneMessage()

    }

    if ((msg.statusCode < 200) || (msg.statusCode >= 300)) {

        fill = 'red'
        return cloneMessage()

    }

    if (msg.statusCode != 200) {

        fill = 'yellow'
        return cloneMessage()

    }

    return null

} finally {

    node.status({
        text: msg.statusCode,
        shape: 'dot',
        fill: fill
    })
}

let model = []

function findScenes(roomId) {

    const scenes = []

    for (const scene of msg.payload.sceneData) {

        if (scene.roomId == roomId) {

            scenes.push({
                name: scene.name,
                id: scene.id
            })
        }
    }

    return scenes.sort((a, b) => {

        return a.name.localeCompare(b.name)

    })
}

for (const room of msg.payload.roomData) {

    const scenes = findScenes(room.id)

    if (scenes.length > 0) {

        model.push({
            name: room.name,
            id: room.id,
            scenes: scenes
        })
    }
}

model = model.sort((a, b) => {

    return a.name.localeCompare(b.name)

})

return {
    payload: model,
    topic: 'powerview/model',
    retain: true
}

const messages = []

for (let address of msg.payload.addresses) {

    const message = RED.util.cloneMessage(msg)

    delete message.payload.addresses
    message.topic = address
    messages.push(message)

}

return [messages]

const messages = []

function errorMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/error',
        retain: false
    }
}

function successMessage(payload) {

    return {
        payload: payload,
        topic: 'hue/' + msg.bridgeAddress + '/key',
        retain: true
    }
}

if (Array.isArray(msg.payload)) {

    for (let result of msg.payload) {

        if (Object.prototype.hasOwnProperty.call(result, 'success')) {

            messages.push(successMessage(result.success.username))

        }

        if (Object.prototype.hasOwnProperty.call(result, 'error')) {

            messages.push(errorMessage(result.error.description))

        }
    }

} else {

    messages.push(errorMessage(msg.payload))

}

node.status({
    text: Object.keys(messages).length,
    shape: 'dot',
    fill: 'blue'
})

return [messages]

const discovered = msg.payload.discovered || {}
const bridges = flow.get('bridges') || {}

for (let address in discovered) {

    const bridge = bridges[address] || {}
    const b = discovered[address]

    for (let property in b) {

        bridge[property] = b[property]

    }

    bridges[address] = bridge

}

for (let topic in msg.payload) {

    const matches = /^hue\/([^ /]+)\/status$/.exec(topic)

    if (Array.isArray(matches) && (matches.length == 2)) {

        const address = matches[1]
        const bridge = bridges[address] || {}

        bridge.status = msg.payload[topic]
        bridges[address] = bridge

    }
}

flow.set('bridges', bridges)

const model = []

for (let address in bridges) {

    const bridge = bridges[address]

    if (bridge.txtRecord) {

        model.push({
            id: bridge.txtRecord.bridgeid,
            model: bridge.txtRecord.modelid,
            name: bridge.name,
            host: bridge.host,
            address: address,
            port: bridge.port,
            status: bridge.status
        })
    }
}

return {
    payload: model,
    topic: 'hue/bridges',
    retain: true
}

const address = msg.payload

return {
    bridgeAddress: address,
    url: 'https://' + address + '/api',
    payload: {
        devicetype: 'nodered',
        generateclientkey: true
    },
    method: 'POST'
}
